+        private static string SanitizeForSendKeys(string text)
+        {
+            System.Text.StringBuilder sb = new();
+            foreach (char c in text)
+            {
+                switch (c)
+                {
+                    case '+': sb.Append("{+}"); break;
+                    case '^': sb.Append("{^}"); break;
+                    case '%': sb.Append("{%}"); break;
+                    case '~': sb.Append("{~}"); break;
+                    case '(': sb.Append("{(}"); break;
+                    case ')': sb.Append("{)}"); break;
+                    case '{': sb.Append("{{}"); break;
+                    case '}': sb.Append("{}}"); break;
+                    case '[': sb.Append("{[}"); break;
+                    case ']': sb.Append("{]}"); break;
+                    case '\r': break;
+                    case '\n': sb.Append("{ENTER}"); break;
+                    default: sb.Append(c); break;
+                }
+            }
+            return sb.ToString();
+        }
