+        private void TypePassword()
+        {
+            try
+            {
+                InterfaceControl? interfaceControl = GetInterfaceControl();
+                if (interfaceControl == null) return;
+
+                // Ensure the control has focus
+                interfaceControl.Focus();
+
+                string password = interfaceControl.Info.Password;
+                if (!string.IsNullOrEmpty(password))
+                {
+                    string escapedPassword = EscapeForSendKeys(password);
+                    SendKeys.SendWait(escapedPassword);
+                }
+            }
+            catch (Exception ex)
+            {
+                Runtime.MessageCollector.AddExceptionMessage("TypePassword (UI.Window.ConnectionWindow) failed", ex);
+            }
+        }
+
+        private void TypeClipboard()
+        {
+            try
+            {
+                InterfaceControl? interfaceControl = GetInterfaceControl();
+                if (interfaceControl == null) return;
+
+                interfaceControl.Focus();
+
+                if (Clipboard.ContainsText())
+                {
+                    string text = Clipboard.GetText();
+                    string escapedText = EscapeForSendKeys(text);
+                    SendKeys.SendWait(escapedText);
+                }
+            }
+            catch (Exception ex)
+            {
+                Runtime.MessageCollector.AddExceptionMessage("TypeClipboard (UI.Window.ConnectionWindow) failed", ex);
+            }
+        }
+
+        private string EscapeForSendKeys(string str)
+        {
+            System.Text.StringBuilder sb = new System.Text.StringBuilder();
+            foreach (char c in str)
+            {
+                if (c == '+' || c == '^' || c == '%' || c == '~' || c == '(' || c == ')' || c == '{' || c == '}' || c == '[' || c == ']')
+                {
+                    sb.Append("{");
+                    sb.Append(c);
+                    sb.Append("}");
+                }
+                else
+                {
+                    sb.Append(c);
+                }
+            }
+            return sb.ToString();
+        }
+
         #endregion

         protected override void WndProc(ref System.Windows.Forms.Message m)
