+            foreach (char c in input)
+            {
+                switch (c)
+                {
+                    case '+':
+                    case '^':
+                    case '%':
+                    case '~':
+                    case '(':
+                    case ')':
+                    case '[':
+                    case ']':
+                    case '{':
+                    case '}':
+                        sb.Append('{').Append(c).Append('}');
