+        private void TypeToActiveConnection(string text)
+        {
+            if (string.IsNullOrEmpty(text)) return;
+
+            try
+            {
+                var connection = _connectionTree.SelectedNode;
+                if (connection.OpenConnections.Count > 0)
+                {
+                    var protocol = connection.OpenConnections[0];
+                    if (protocol == null) return;
+
+                    // Focus the connection
+                    protocol.Focus();
+
+                    string escapedText = EscapeForSendKeys(text);
+                    System.Windows.Forms.SendKeys.SendWait(escapedText);
+                }
+            }
+            catch (Exception ex)
+            {
+                Runtime.MessageCollector.AddExceptionStackTrace("Type to connection failed", ex);
+            }
+        }
