+        private string EscapeForSendKeys(string input)
+        {
+            if (string.IsNullOrEmpty(input)) return input;
+
+            System.Text.StringBuilder sb = new System.Text.StringBuilder();
+            foreach (char c in input)
+            {
+                switch (c)
+                {
+                    case '+':
+                    case '^':
+                    case '%':
+                    case '~':
+                    case '(':
+                    case ')':
+                    case '[':
+                    case ']':
+                    case '{':
+                    case '}':
+                        sb.Append('{').Append(c).Append('}');
+                        break;
+                    default:
+                        sb.Append(c);
+                        break;
+                }
+            }
+            return sb.ToString();
+        }
